import axios from 'axios';

const weatherApiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;
const googleMapsApiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

export const getWeather = async (location) => {
  const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${weatherApiKey}`);
  return response.data;
};

export const getMap = (container, options) => {
  const map = new window.google.maps.Map(container, options);
  return map;
};
