import axios from 'axios';

const API_KEY = process.env.REACT_APP_MAP_API_KEY;

export const getWeather = async (location, startDate) => {
  try {
    const response = await axios.get(`https://api.weatherapi.com/v1/history.json`, {
      params: {
        key: API_KEY,
        q: location,
        dt: startDate
      }
    });

    const weatherData = response.data.forecast.forecastday.map(day => ({
      date: day.date,
      temp: day.day.avgtemp_c,
      description: day.day.condition.text,
    }));

    return weatherData;
  } catch (error) {
    console.error('Error fetching weather data:', error);
    return [];
  }
};

// Example getMap function
export const getMap = async (location) => {
  try {
    const response = await axios.get(`https://api.mapbox.com/styles/v1/mapbox/streets-v11/static/${location.longitude},${location.latitude},14,0/600x600?access_token=${API_KEY}`);
    console.log('Map API response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error fetching map data:', error);
    return null;
  }
};
