import axios from 'axios';

const WEATHER_API_KEY = process.env.REACT_APP_WEATHER_API_KEY;
const MAP_API_KEY = process.env.REACT_APP_MAP_API_KEY;

export const getWeather = async (location, startDate) => {
  try {
    const response = await axios.get(`https://api.weatherapi.com/v1/history.json`, {
      params: {
        key: WEATHER_API_KEY,
        q: location,
        dt: startDate
      }
    });

    const weatherData = response.data.forecast.forecastday.map(day => ({
      date: day.date,
      temp: day.day.avgtemp_c,
      description: day.day.condition.text,
    }));

    return weatherData;
  } catch (error) {
    console.error('Error fetching weather data:', error);
    return [];
  }
};

export const getMap = async (location) => {
  try {
    // Construct the image URL for Mapbox static map API
    const mapUrl = `https://api.mapbox.com/styles/v1/mapbox/streets-v11/static/${location.longitude},${location.latitude},14,0/600x600?access_token=${MAP_API_KEY}`;
    console.log('Map URL:', mapUrl);
    return mapUrl;
  } catch (error) {
    console.error('Error fetching map data:', error);
    return null;
  }
};
